function preload(){particleImage=loadImage("res//squareparts.png")}function setup(){if(pageLoaded){h=$(".scrollDiv")[0].offsetTop,aboutCanvas=createCanvas(windowWidth,h);for(var t=0;t<100;t++)blueParticles[t]=new BlueParticle}}function draw(){clear();for(var t=0;t<blueParticles.length;t++)blueParticles[t].show()}function BlueParticle(){this.z=random(0,20),this.x=random(0,width),this.y=random(0,height),this.width=map(this.z,0,20,.5,15),this.yspeed=map(this.z,0,20,1,10),this.show=function(){image(particleImage,this.x,this.y,this.width,this.width)},this.moveUp=function(){this.y<0&&(this.y=random(height+200,height)),this.y=this.y-this.yspeed},this.moveDown=function(){this.y>height&&(this.y=random(-200,0)),this.y=this.y+this.yspeed}}function windowResized(){setup()}var blueParticles=[],aboutCanvas,h,particleImage,lastScrollTop=0;$(window).on("scroll",function(){var t=$(this).scrollTop();if(lastScrollTop>t)for(var i=0;i<blueParticles.length;i++)blueParticles[i].moveDown();if(lastScrollTop<t)for(var i=0;i<blueParticles.length;i++)blueParticles[i].moveUp();lastScrollTop=t});